mapper.select:
SELECT *
FROM
(
SELECT rownum as rnum, sub_query.*
FROM
(
SELECT posts.*, users.NICKNAME
FROM posts
INNER JOIN users ON posts.user_id = users.user_id
where sub_category_id = #{subCategoryId}
ORDER BY post_id DESC
) sub_query
WHERE rownum  <=  #{pageEnd}
)
WHERE rnum  >=  #{pageStart}

select tag_id
from hashtag
where tag_name = #{tagname}

mapper.insert:
insert all into post_hash 

		values ( #{post_id}, (select tag_id from hashtag where tag_name=#{tag_name})) 

select * from dual;
